INSERT INTO `albums` (`albumid`, `title`, `description`, `releasedate`, `price`, `label`, `thumbnail`, `producerid`, `userid`) VALUES
(0, 'N/A', '<p>N/A</p>', '0000-00-00', 0, 'N/A', 'https://png.pngtree.com/element_origin_min_pic/16/08/08/0957a7e677c6791.jpg', 0, 0),
(2, '1234', '<p><span style=\"font-weight: bolder; color: rgb(0, 0, 0); font-family: baomoi, &quot;San Francisco&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 20px;\">Chi Dân</span><span style=\"color: rgb(0, 0, 0); font-family: baomoi, &quot;San Francisco&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 20px;\">&nbsp;đã chính thức ra mắt MV&nbsp;</span><em style=\"color: rgb(0, 0, 0); font-family: baomoi, &quot;San Francisco&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 20px;\">1, 2, 3, 4</em><span style=\"color: rgb(0, 0, 0); font-family: baomoi, &quot;San Francisco&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 20px;\">&nbsp;đậm chất ngôn tình với hình ảnh lãng mạn, dễ thương cùng giai điệu bắt tai và ca từ dễ thấm. Đây là 1 ca khúc được sáng tác và hòa âm phối khí bởi nhạc sĩ Nguyễn Đình Vũ.</span></p><p><span style=\"color: rgb(0, 0, 0); font-family: baomoi, &quot;San Francisco&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 20px;\">Với sản phẩm lần này, Chi Dân cũng chia sẻ rằng bản thân không ngại đầu tư kinh phí và các thiết bị kĩ thuật hiện đại để có được những hình ảnh hoàn hảo nhất có thể. Nội dung MV kể về cặp đôi đã chia tay nhưng chàng trai vẫn còn yêu cô gái và tìm mọi cách để ở cạnh cô. Nhưng không may, với sự vụng về của anh, mọi thứ đều bị phá hỏng.</span></p><p class=\"body-text\" style=\"margin-bottom: 15px; max-width: 100%; color: rgb(0, 0, 0); font-family: baomoi, &quot;San Francisco&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 20px;\">Nhân vật của Chi Dân trong MV được đạo diễn Gin Trần viết \"đo ni đóng giày\" cho chính nam ca sĩ, một người lạc quan sôi nổi luôn sống và yêu hết mình.</p><p class=\"body-text\" style=\"margin-bottom: 15px; max-width: 100%; color: rgb(0, 0, 0); font-family: baomoi, &quot;San Francisco&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 20px;\">Để có những thước phim đẹp nhất, Chi Dân đã không ngần ngại dành nhiều thời gian cùng đạo diễn dựng những khung hình độc đáo. Bối cảnh quay sẽ chỉ dành riêng cho MV của anh và sẽ không xuất hiện trong các MV khác.</p><p><span style=\"color: rgb(0, 0, 0); font-family: baomoi, &quot;San Francisco&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 20px;\"><br></span><br></p>', '2018-11-08', 100, 'Chi Dân', 'https://res.cloudinary.com/silentlove995/image/upload/c_scale,o_100,q_auto:eco,w_658,z_0.4/v1541693881/avatar/cover1541693868370.png', 2, 3),
(3, 'Bởi vì em hết yêu anh', '<p>Single \"Bởi Vì Em Hết Yêu Anh\" là ca khúc mới nhất mà Chi Dân đồng sáng tác cùng người anh Chiến Lê. Hy vọng đây sẽ là bản HIT mới của anh chàng Chi Dân trong năm 2015 này.<br></p>', '2018-11-08', 100, 'Chi Dân', 'https://res.cloudinary.com/silentlove995/image/upload/c_scale,o_100,q_auto:eco,w_658,z_0.4/v1541694814/avatar/319301541694807291.png', 2, 3),
(4, 'Có được không em', '<p class=\"body-text\" style=\"margin-bottom: 15px; max-width: 100%; color: rgb(0, 0, 0); font-family: baomoi, &quot;San Francisco&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 20px;\">Chiều 13/03,&nbsp;<span style=\"font-weight: bolder;\">ca sĩ Chi Dân</span>&nbsp;– người sáng tác ra một loạt hit đình đám như: Làm vợ anh nhé, Điều anh biết, Người tôi yêu... đã giới thiệu dự án âm nhạc mới mang tên&nbsp;<span style=\"font-weight: bolder;\">\"Có được không em\"</span>.</p><p class=\"body-text\" style=\"margin-bottom: 15px; max-width: 100%; color: rgb(0, 0, 0); font-family: baomoi, &quot;San Francisco&quot;, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 20px;\">Với giai điệu vui tươi cùng ca từ dễ nhớ, ca khúc là&nbsp;<span style=\"font-weight: bolder;\">lời tỏ tình ngọt ngào</span>của chàng trai dành cho cô gái mình yêu thầm. MV \"ra lò\" đúng dịp Valentine Trắng nên càng được giới trẻ thích thú truyền tay nhau. Nhờ vậy mà chỉ sau 24h ra mắt, MV đã có hơn 1 triệu lượt xem trên YouTube.</p>', '2018-11-08', 150, 'Chi Dân', 'https://res.cloudinary.com/silentlove995/image/upload/c_scale,o_100,q_auto:eco,w_658,z_0.4/v1541697182/avatar/cover1541697172420.png', 3, 3),
(5, 'Điều anh biết', '<p>Single mới nhất của ca sĩ Chi Dân mang tên \'Điều Anh Biết\', một sáng tác mới của Nhạc sĩ Ân Nhi.<br></p>', '2018-11-08', 120, 'Chi Dân', 'https://png.pngtree.com/element_origin_min_pic/16/08/08/0957a7e677c6791.jpg', 4, 3),
(6, 'Dịu Dàng Đến Từng Phút Giây', '<p>Nằm trong CD “Ngôi nhà hoa hồng” đình đám ra mắt năm 2008 kết hợp với nữ ca sĩ Bảo Thy, “Dịu dàng đến từng phút giây” được coi là bản hit lớn của Quang Vinh cho tới tận bây giờ. Giai điệu rộn ràng cùng phần ca từ ngọt ngào về cảm xúc của chàng trai trong tình yêu khiến cho sáng tác của nhạc sĩ Lương Bằng Quang nhận được nhiều sự đồng cảm từ phía người hâm mộ.</p><p><br></p><p>Sau đúng 10 năm, “Dịu dàng đến từng phút giây” lại một lần nữa được “sống dậy” qua sự kết hợp lần đầu tiên trong âm nhạc của Quang Vinh và Chi Dân trong tập 10 series “Greatest Hits/ The Memories” khiến người hâm mộ không khỏi thích thú. Thêm vào đó, bản cover lần này cũng được Quang Vinh làm mới lại theo phong cách EDM thịnh hành thay vì giữ nguyên bản phối pop quen thuộc gắn liền với tuổi thanh xuân thế hệ 8x, 9x. Chia sẻ về bản cover “Dịu dàng đến từng phút giây”, Quang Vinh cho biết đây được coi như món quà anh gửi tặng tới người hâm mộ trong ngày cuối cùng của năm 2017, đồng thời anh còn gọi lệch đi đầy hóm hỉnh là “Tưng bừng tới từng phút giây”.</p>', '2018-11-08', 130, 'Chi Dân', 'https://res.cloudinary.com/silentlove995/image/upload/c_scale,o_100,q_auto:eco,w_658,z_0.4/v1541698119/avatar/cover1541698096202.png', 5, 3),
(9, 'Làm vợ anh nhé', '<p>N/A</p>', '2018-11-08', 140, 'Chi Dân', 'https://res.cloudinary.com/silentlove995/image/upload/c_scale,o_100,q_auto:eco,w_658,z_0.4/v1541698629/avatar/326681541698623350.png', 3, 3),
(10, 'Còn gì nữa', '<p>N/A</p>', '2018-11-08', 500, 'Chi Dân', 'https://png.pngtree.com/element_origin_min_pic/16/08/08/0957a7e677c6791.jpg', 2, 3),
(11, 'Mất trí nhớ', '<p>N/A</p>', '2018-11-08', 1200, 'Chi Dân', 'https://res.cloudinary.com/silentlove995/image/upload/c_scale,o_100,q_auto:eco,w_658,z_0.4/v1541700519/avatar/26788_Mat-Tri-Nho-Chi-Dan1541700512337.png', 2, 3),
(12, 'Những bài hát hay nhất của Mr Siro', '<p>Tổng hợp những bài hát hay nhất của Mr Siro, hay và buồn đến nao lòng.</p>', '2018-11-08', 2000, 'Mr Siro Entertainment', 'https://res.cloudinary.com/silentlove995/image/upload/c_scale,o_100,q_auto:eco,w_658,z_0.4/v1541704023/avatar/mr_siro1541704016760.png', 8, 3),
(13, 'Những bài hát hay nhất của Hương Tràm', '<p>Tổng hợp những bài hit của quán quân Giọng hát Việt 2012.</p>', '2018-11-08', 1200, 'Hương Tràm', 'https://res.cloudinary.com/silentlove995/image/upload/c_scale,o_100,q_auto:eco,w_658,z_0.4/v1541711191/avatar/Duy%C3%AAn_m%C3%ACnh_l%E1%BB%A11541711186100.png', 9, 3),
(14, 'Nhạc Hoa tuyển chọn', '<p>Tuyển chọn những bản nhạc Hoa hay và hot nhất.</p>', '2018-11-08', 200, 'Nhạc Hoa', 'https://res.cloudinary.com/silentlove995/image/upload/c_scale,o_100,q_auto:eco,w_658,z_0.4/v1541716417/avatar/girl_xinh_021541716408008.png', 15, 3),
(15, 'Nhạc vàng tuyển chọn', '<p>Tuyển chọn những bài nhạc vàng hay nhất, gợi lại những nỗi niềm mộc mạc xa xưa.</p>', '2018-11-08', 2300, 'Genesis Construct Entertainment', 'https://png.pngtree.com/element_origin_min_pic/16/08/08/0957a7e677c6791.jpg', 17, 3);

